function createFly(){if("wx"===mpvuePlatform){return new(require("flyio/dist/npm/wx"))}return null}export function get(e,o={}){const t=createFly();if(t)return new Promise((r,n)=>{t.get(e,o).then(e=>{e&&e.data&&0===e.data.error_code?(console.log(e.data),r(e)):(mpvue.showToast({title:"失败惹",duration:2e3}),n(e))}).catch(e=>{console.log(e),handleError(e),n(e)})})}function handleError(e){console.log(e)}export function post(e,o={}){const t=createFly();if(t)return new Promise((r,n)=>{t.post(e,o).then(e=>{console.log(e),r(e)}).catch(e=>{console.log(e),handleError(e),n(e)})})}